css:
.heading {

}

.company-info {
    display: flex;
}

.company-info h1 {
    margin-left: 10px;
    font-size: 30px;
}

.items-table {
    width: 100%;
    border-collapse: collapse;
}

.items-table td {
    border: 1px solid black;
}

.items-table {
    margin-bottom: 40px;
    margin-top: 40px;
    counter-reset: serial-number;  /* Set the serial number counter to 0 */
}

.items-table tbody tr td:first-child:before {
  counter-increment: serial-number;  /* Increment the serial number counter */
  content: counter(serial-number);  /* Display the counter */
}

.sub-heading {
}

.info-table {
    width: 100%;
    font-size: 13px;
}
